        -- If there are no more records, exit the loop
        IF done THEN
            LEAVE read_loop;
        END IF;
        
        -- Update the price of the current product
        UPDATE products
        SET price = p_newPrice
        WHERE productID = currentProductID;
        
    END LOOP;
    
    -- Close the cursor after completing the loop
    CLOSE product_cursor;
    
END$$

